    phase = (phase + 1) % phases.length;\n            count = 0;\n        }\n    }\n    \n    const interval = setInterval(updateDisplay, 1000);\n    \n    setTimeout(() => {\n        clearInterval(interval);\n        event.target.innerHTML = \'Start Again\';\n    }, 120000);\n}\n</script>','Emergency Breathing','','inherit','closed','closed','','41-revision-v1','','','2025-09-19 07:09:37','2025-09-19 11:09:37','',41,'http://localhost:8000/?p=44',0,'revision','',0),(45,0,'2025-09-19 07:10:25','2025-09-19 11:10:25','<style>\n.emergency-page {\n    background: linear-gradient(135deg, #48bb78 0%, #38a169 100%);\n    min-height: 50vh;\n    padding: 20px 0;\n    margin: -20px -20px 20px -20px;\n}\n\n.emergency-container {\n    max-width: 800px;\n    margin: 0 auto;\n    padding: 40px 20px;\n    background: white;\n    border-radius: 20px;\n    box-shadow: 0 20px 60px rgba(0,0,0,0.1);\n}\n\n.emergency-header {\n    text-align: center;\n    margin-bottom: 40px;\n}\n\n.emergency-icon {\n    font-size: 4rem;\n    color: #48bb78;\n    margin-bottom: 20px;\n}\n\n.emergency-title {\n    color: #2d3748;\n    font-size: 2.5rem;\n    margin-bottom: 15px;\n    font-weight: 700;\n}\n\n.emergency-subtitle {\n    color: #4a5568;\n    font-size: 1.2rem;\n    line-height: 1.6;\n}\n\n.technique-card {\n    background: #f0fff4;\n    border-radius: 15px;\n    padding: 30px;\n    margin-bottom: 25px;\n    border-left: 5px solid #48bb78;\n}\n\n.technique-title {\n    color: #2d3748;\n    font-size: 1.5rem;\n    margin-bottom: 15px;\n    font-weight: 600;\n}\n\n.technique-steps {\n    list-style: none;\n    padding: 0;\n}\n\n.technique-steps li {\n    background: white;\n    padding: 15px 20px;\n    margin-bottom: 10px;\n    border-radius: 10px;\n    border-left: 3px solid #48bb78;\n    font-size: 1.1rem;\n    line-height: 1.6;\n}\n\n.sensory-grid {\n    display: grid;\n    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));\n    gap: 15px;\n    margin-top: 20px;\n}\n\n.sensory-item 